            var actionId = Math.floor(Math.random() * 100000) + 1;

            // Site
            var actionAffiliation = 'mydisneyworld.ru';

            // Product price
            var price = parseInt($.trim(popup.find('.tover-popup-title').find('.pink').text().replace('—Ä—É–±.', '').replace(' ', '')));

            // Product name
            var name = $.trim(popup.find('.tover-popup-title').html().split('<')[0].replace('‚Äî', ''));

            // Products SKU
            var sku = popup.attr('id').split('-')[1];

            // Product quantity
            var quantity = 1;

            // Set variable to local storage
            localStorage.setItem('dataLayer', JSON.stringify({
                actionId: actionId,
                actionAffiliation: actionAffiliation,
                actionTotal: price,
                actionProducts: [
                    {
                        sku: sku,
                        name: name,
                        price: price,
                        quantity: quantity
                    }
                ],
            }));
